    onTreepanelBeforeExpand: function(p, animate, eOpts) {
        var treeid = p.getId();

        Ext.Ajax.request(
        {
            url : 'get_UserRightList',
            params :
            {
                url : treeid
            },
            success : function (response){
                var result=Ext.decode(response.responseText);
                //console.log(result);
                if(!result){
                    Ext.Msg.alert('权限不足', '您没有权限进行此操作');
                    p.disable();
                }
            },
            failure : function (response){
                Ext.Msg.alert('权限不足', '您没有权限进行此操作');
                p.collapse();
                p.disable();
            }
        });


    },

