    onDb_UserWindowFormAfterRender: function(component, eOpts) {
        //获取当前注册时间
        var registerdateField = Ext.getCmp('userAdd_registerdate');
        var dateStr = "";
        var myDate = new Date();
        dateStr += myDate.getFullYear();    //获取完整的年份(4位,1970-????)
        dateStr += "-" + (myDate.getMonth()+1);       //获取当前月份(0-11,0代表1月)
        dateStr += "-" +myDate.getDate();        //获取当前日(1-31)
        registerdateField.setValue(dateStr);
    },

    onButtonClick1: function(button, e, eOpts) {
        var win = Ext.getCmp('db_UserInfoAddWindow');
        win.close();
    },

    onButtonClick: function(button, e, eOpts) {
        var win  = Ext.getCmp('db_UserInfoAddWindow');

        //验证表单
        //验证密码
        var pwd = Ext.getCmp('userAdd_userPwd').getValue();
        var repwd = Ext.getCmp('userAdd_rePwd').getValue();
        if(pwd != repwd){
            Ext.Msg.alert('提示', '两次输入的密码不同，请检查。');
            return;
        }


        var myform = Ext.getCmp('db_UserAddWindowForm').getForm();
        if (myform.isValid())
        { // make sure the form contains valid data before submitting
            //使得重复密码框失效
            var repwd = Ext.getCmp('userAdd_rePwd');
            repwd.disable();

            myform.submit({

                url : 'add_UserInfo',
                success : function (form, action)
                {
                    Ext.Msg.alert('成功', '添加用户成功。');

                    var mystore = Ext.StoreMgr.get('uUserInfoStore'); //获得store对象
                    mystore.reload();
                    win.close();
                },
                failure: function(form, action){
                    Ext.Msg.alert('失败', '添加用户失败，请重试。');
                    win.close();
                }
            });
        }
        else
        {
            Ext.Msg.alert('注意', '填写的信息有误，请检查！');
        }
    }

