Ext.define('MyApp.view.infoCommentTab', {
    extend: 'Ext.panel.Panel',
    alias: 'widget.infoCommentTab',

    requires: [
        'Ext.form.Panel',
        'Ext.grid.Panel',
        'Ext.grid.RowNumberer',
        'Ext.grid.column.Date',
        'Ext.grid.View',
        'Ext.toolbar.Toolbar',
        'Ext.button.Button',
        'Ext.selection.CheckboxModel'
    ],

    id: 'infoCommentTab',
    layout: 'fit',
    title: '文章评论-详细',

    initComponent: function() {
        var me = this;

        Ext.applyIf(me, {
            items: [
                {
                    xtype: 'form',
                    height: 400,
                    width: 800,
                    layout: 'fit',
                    bodyPadding: 10,
                    items: [
                        {
                            xtype: 'gridpanel',
                            id: 'commentGrid',
                            autoScroll: true,
                            title: '',
                            forceFit: true,
                            store: 'InfoCommentStore',
                            columns: [
                                {
                                    xtype: 'rownumberer',
                                    width: 38
                                },
                                {
                                    xtype: 'gridcolumn',
                                    width: 103,
                                    dataIndex: 'commentAuthor',
                                    text: '评论人'
                                },
                                {
                                    xtype: 'datecolumn',
                                    width: 100,
                                    dataIndex: 'commentTime',
                                    text: '评论时间'
                                },
                                {
                                    xtype: 'gridcolumn',
                                    width: 556,
                                    dataIndex: 'commentContent',
                                    text: '评论内容'
                                }
                            ],
                            dockedItems: [
                                {
                                    xtype: 'toolbar',
                                    dock: 'top',
                                    enableOverflow: true,
                                    items: [
                                        {
                                            xtype: 'button',
                                            handler: function(button, event) {
                                                var grid = Ext.getCmp('commentGrid');

                                                var record = grid.getSelectionModel().getSelection();

                                                if(record.length === 0){
                                                    Ext.Msg.alert('提示','请先选择您要操作的行！');
                                                    return;

                                                }else{



                                                    var  commentIds =new Array(record.length);
                                                    for(var i = 0;i<record.length;i++){
                                                        commentIds[i] = record[i].get("commentId");

                                                    }


                                                    Ext.Ajax.request({
                                                        url:'update_comment',
                                                        params:{commentIds:commentIds},

                                                        success:function(response){
                                                            Ext.Msg.alert('成功','删除评论');
                                                            var mystore = Ext.StoreMgr.get('InfoCommentStore');
                                                            mystore.load();


                                                        }

                                                    });

                                                }
                                            },
                                            icon: 'images/table_delete.png',
                                            text: '删除'
                                        }
                                    ]
                                }
                            ],
                            selModel: Ext.create('Ext.selection.CheckboxModel', {

                            })
                        }
                    ]
                }
            ]
        });

        me.callParent(arguments);
    }

});