Ext.define('MyApp.view.tpMain', {
    extend: 'Ext.tree.Panel',
    alias: 'widget.tpmain',

    id: 'tpMain',
    collapsible: true,
    title: '项目一览',
    store: 'treestoreMain',
    rootVisible: false,

    initComponent: function() {
        var me = this;

        Ext.applyIf(me, {
            viewConfig: {
                listeners: {
                    itemclick: {
                        fn: me.onViewItemClick,
                        scope: me
                    }
                }
            }
        });

        me.callParent(arguments);
    },

    onViewItemClick: function(dataview, record, item, index, e, eOpts) {
    	Ext.getCmp("body").getLayout().setActiveItem(0); 
    	switch(record.raw.id)
        {
            case 101:
            {       
		        Ext.getCmp("main").getLayout().setActiveItem(1);
		        var head = document.getElementsByTagName('head')[0]; 
		        var script= document.createElement("script"); 
		        script.type = "text/javascript"; 
		        script.src="MyMap/TotalMap.js"; 
		        head.appendChild(script);
		        break;
            }
            case 102:
        	{
            	Ext.getCmp("main").getLayout().setActiveItem(2);
            	break;
        	}
        }
        
    }

});