Ext.define('MyApp.view.use_SelectLayerWindow', {
    extend: 'Ext.window.Window',
    alias: 'widget.use_SelectLayerWindow',

    requires: [
        'Ext.grid.Panel',
        'Ext.grid.RowNumberer',
        'Ext.grid.View',
        'Ext.selection.CheckboxModel',
        'Ext.toolbar.Toolbar',
        'Ext.button.Button',
        'Ext.toolbar.Fill',
        'Ext.form.field.Text',
        'Ext.toolbar.Spacer',
        'Ext.form.field.Hidden'
    ],

    height: 379,
    id: 'use_SelectLayerWindow',
    width: 628,
    layout: 'fit',
    title: '选择抽样图层',

    initComponent: function() {
        var me = this;

        Ext.applyIf(me, {
            items: [
                {
                    xtype: 'gridpanel',
                    id: 'use_selectLayerGrid',
                    autoScroll: true,
                    store: 'useMapResourceStore',
                    columns: [
                        {
                            xtype: 'rownumberer'
                        },
                        {
                            xtype: 'gridcolumn',
                            width: 100,
                            dataIndex: 'rightName',
                            text: '权限名称'
                        },
                        {
                            xtype: 'gridcolumn',
                            width: 250,
                            dataIndex: 'url',
                            text: '权限地址'
                        },
                        {
                            xtype: 'gridcolumn',
                            width: 200,
                            dataIndex: 'description',
                            text: '描述'
                        }
                    ],
                    selModel: Ext.create('Ext.selection.CheckboxModel', {

                    }),
                    dockedItems: [
                        {
                            xtype: 'toolbar',
                            dock: 'top',
                            items: [
                                {
                                    xtype: 'button',
                                    text: '确认',
                                    listeners: {
                                        click: {
                                            fn: me.onButtonClick1,
                                            scope: me
                                        }
                                    }
                                },
                                {
                                    xtype: 'tbfill'
                                },
                                {
                                    xtype: 'textfield',
                                    fieldLabel: '地图资源索引',
                                    labelWidth: 80,
                                    blankText: 'mapresources',
                                    emptyText: 'mapresources'
                                },
                                {
                                    xtype: 'button',
                                    text: '查询'
                                },
                                {
                                    xtype: 'tbspacer',
                                    width: 20
                                },
                                {
                                    xtype: 'button',
                                    text: '取消',
                                    listeners: {
                                        click: {
                                            fn: me.onButtonClick,
                                            scope: me
                                        }
                                    }
                                },
                                {
                                    xtype: 'hiddenfield',
                                    id: 'hide_useSampling_whichLayer',
                                    fieldLabel: 'Label'
                                }
                            ]
                        }
                    ]
                }
            ]
        });

        me.callParent(arguments);
    },

    onButtonClick1: function(button, e, eOpts) {
        var grid = Ext.getCmp('use_selectLayerGrid');
        var records =grid.getSelectionModel().getSelection();//获取到表上的那一列
        if(records.length > 1){
            Ext.MessageBox.show({
                title:"提示",
                msg:"只能选择一行数据",
                icon: Ext.MessageBox.INFO,
                buttons: Ext.Msg.OK
            });
            return;
        }else if(records.length < 1){
            Ext.MessageBox.show({
                title:"提示",
                msg:"请选择一个图层资源。",
                icon: Ext.MessageBox.INFO,
                buttons: Ext.Msg.OK
            });
            return;
        }

        var mapUrl = records[0].get('url');	//取得地图资源url

        var layerName = Ext.getCmp('hide_useSampling_whichLayer').getValue();
        switch(layerName){
            case "ditu":
            Ext.getCmp('text_useSampling_ditu').setValue(mapUrl);
            break;
            case "fanwei":
            Ext.getCmp('text_useSampling_fanwei').setValue(mapUrl);
            break;
            case "zhishi":
            Ext.getCmp('text_useSampling_zhishi').setValue(mapUrl);
            break;
            case "baogao":
            Ext.getCmp('text_useSampling_baogao').setValue(mapUrl);
            break;
            case "yangben":
            Ext.getCmp('text_useSampling_yangben').setValue(mapUrl);
            break;
            default:
            return;
        }
        var win = Ext.getCmp('use_SelectLayerWindow');
        win.close();


    },

    onButtonClick: function(button, e, eOpts) {
        var win = Ext.getCmp('use_SelectLayerWindow');
        win.close();
    }

});