/*Table structure for table `info_report` */

DROP TABLE IF EXISTS `info_report`;

CREATE TABLE `info_report` (
  `report_id` int(10) NOT NULL AUTO_INCREMENT COMMENT '投诉举报id',
  `reporter_name` varchar(30) DEFAULT NULL COMMENT '举报人姓名',
  `report_title` varchar(30) DEFAULT NULL COMMENT '标题',
  `report_class` varchar(30) DEFAULT NULL COMMENT '类别',
  `report_time` date DEFAULT NULL COMMENT '来信时间',
  `reported_time` date DEFAULT NULL COMMENT '问题发生时间',
  `report_content` text COMMENT '内容',
  `reporter_email` varchar(30) DEFAULT NULL COMMENT '举报人电子邮件',
  `reporter_address` varchar(50) DEFAULT NULL COMMENT '举报人地址',
  `reporter_zipcode` varchar(30) DEFAULT NULL COMMENT '邮编',
  `reporter_tel` varchar(20) DEFAULT NULL COMMENT '举报人电话',
  `reporter_idnum` varchar(20) DEFAULT NULL COMMENT '来信人身份证号',
  `reported_place` varchar(50) DEFAULT NULL COMMENT '问题发生地点',
  `reported_departlevel` varchar(30) DEFAULT NULL COMMENT '被举报人单位级别',
  `reported_departname` varchar(20) DEFAULT NULL COMMENT '被举报人单位',
  `reported_name` varchar(20) DEFAULT NULL COMMENT '被举报人姓名',
  `reported_attachment` varchar(100) DEFAULT NULL COMMENT '被举报人职务',
  `report_replyer` varchar(20) DEFAULT NULL COMMENT '回复人',
  `report_replytime` date DEFAULT NULL COMMENT '回复时间',
  `report_replycontent` text COMMENT '回复内容',
  `report_isreply` varchar(10) DEFAULT '否' COMMENT '是否回复',
  PRIMARY KEY (`report_id`)
